mapper.select:
select * 
  from tb_user
 where id=#{id}
   and password =#{password}

select count(*)
  from tb_user
 where id=#{id}

select count(*)
  from tb_user
 where id=#{id}
   and name=#{name}

mapper.update:
update tb_user
   set password=#{password}
  where id=#{id}
    and name=#{name}

update tb_user
   set password=#{password},
   	   birth=#{birth},
   	   contact=#{contact},
   	   nat=#{nat}
 where id=#{id}
